script.#text:
$('.chat').on('click', function (e) {
  e.preventDefault();

  $(this).text('Loading...');

  setTimeout(function () {
    $('.chat').text('Live Chat');
  }, 8000);

  function load_live_agent () {
    var initESW = function (gslbBaseURL) {
      embedded_svc.settings.language = 'en-US';
      embedded_svc.settings.displayHelpButton = false;

      embedded_svc.settings.defaultMinimizedText = 'Chat with a Representative';
      embedded_svc.settings.disabledMinimizedText = 'Representative Offline';

      embedded_svc.settings.entryFeature = 'LiveAgent';
      embedded_svc.settings.enabledFeatures = ['LiveAgent'];

      embedded_svc.init(
        'https://crossway.my.salesforce.com',
        'https://crossway.force.com/support',
        gslbBaseURL,
        '00D6A000000eajF',
        'Customer_Service',
        {
          baseLiveAgentContentURL: 'https://c.la4-c2-ph2.salesforceliveagent.com/content',
          deploymentId: '5726A000000921o',
          buttonId: '5736A0000008oO6',
          baseLiveAgentURL: 'https://d.la4-c2-ph2.salesforceliveagent.com/chat',
          eswLiveAgentDevName: 'Customer_Service',
          isOfflineSupportEnabled: true
        }
      );

      embedded_svc.addEventHandler('afterInit', function () {
        embedded_svc.onHelpButtonClick();
      });
    };

    if (!window.embedded_svc) {
      var s = document.createElement('script');

      s.setAttribute('src', 'https://crossway.my.salesforce.com/embeddedservice/5.0/esw.min.js');

      s.onload = function () {
        initESW();
      };

      document.body.appendChild(s);
    } else {
      initESW();
    }
  }

  if (!window.live_agent_loaded) {
    var js_el = document.createElement('script');

    js_el.setAttribute('src', 'https://crossway.my.salesforce.com/embeddedservice/5.0/esw.min.js');

    js_el.onload = function () {
      window.live_agent_loaded = true;

      load_live_agent();
    };

    document.body.appendChild(js_el);

    var link_params = {
      rel: 'stylesheet',
      type: 'text/css',
      href: '/static/css/live-agent.css'
    };

    $('<link/>', link_params).appendTo('body');
  } else {
    embedded_svc.onHelpButtonClick();
  }
});
